import React from 'react'
import './styles/Register.css'
import { register } from '../actions/register'
import { connect } from 'react-redux'

class Register extends React.Component {
    constructor() {
        super()
        this.state = {
            username: "",
            email: "",
            password: "",
            first_name: "",
            last_name: "",
        }
    }

    editRegisterForm = event => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    register = event => {
        event.preventDefault()
        this.props.register(this.state).then(success => {
            if (success) this.setState({
                username: "",
                email: "",
                password: "",
                first_name: "",
                last_name: "",
            })
        })
    }

    render = () => {
        console.log(this.props.status)

        return (
        <div className="register-view">
            <h1>Let's Create Your Profile!</h1>
            <form>

                <input 
                    type='text'
                    placeholder='Username'
                    minLength='3' 
                    required
                    value={this.state.username}
                    onChange={this.editRegisterForm}
                    name='username'
                />  
                  <input 
                    type='text'
                    placeholder='firstName'
                    minLength='3' 
                    required
                    value={this.state.firstName}
                    onChange={this.editRegisterForm}
                    name='firstName'
                />   
                  <input 
                    type='text'
                    placeholder='lastName'
                    minLength='3' 
                    required
                    value={this.state.lastName}
                    onChange={this.editRegisterForm}
                    name='lastName'
                />             

                <input 
                    type='email'
                    placeholder='Email' 
                    required
                    value={this.state.email}
                    onChange={this.editRegisterForm}
                    name='email'
                />             

                <input 
                    minLength='6'
                    type='password'
                    placeholder='Password' 
                    required
                    value={this.state.password}
                    onChange={this.editRegisterForm}
                    name='password'
                />
                <button onClick={this.register}>Done</button>
            </form>

            <p>{this.props.message}</p>
        </div>)
    }
}


const mapStatetoProps = state => ({
    message: state.register.message,
    token: state.register.token,
    isRegistering: state.register.isRegistering,
    error: state.register.error,
    status: state.register.status
})

export default connect(mapStatetoProps, { register })(Register)
